# Review and Approve Learned Skills# Helps review session patterns detected by continuous learning skillparam(    [switch]$Approve,    [string]$SessionId)$LearnedSkillsPath = Join-Path $env:USERPROFILE ".claude\skills\learned"if (-not (Test-Path $LearnedSkillsPath)) {    Write-Host "No learned skills found at: $LearnedSkillsPath" -ForegroundColor Yellow    exit 0}$sessionFiles = Get-ChildItem -Path $LearnedSkillsPath -Filter "session_*.json" | Sort-Object LastWriteTime -Descendingif ($sessionFiles.Count -eq 0) {    Write-Host "No pending sessions to review" -ForegroundColor Gray    exit 0}Write-Host ("=" * 80) -ForegroundColor CyanWrite-Host "LEARNED SKILLS REVIEW" -ForegroundColor CyanWrite-Host ("=" * 80) -ForegroundColor Cyanforeach ($file in $sessionFiles) {    $session = Get-Content $file.FullName -Raw | ConvertFrom-Json        if ($session.status -eq "approved") {        continue    }        Write-Host "`nSession: $($session.timestamp)" -ForegroundColor White    Write-Host "Messages: $($session.message_count)" -ForegroundColor Gray    Write-Host "Patterns detected: $($session.patterns_detected.Count)" -ForegroundColor Yellow        foreach ($pattern in $session.patterns_detected) {        $color = switch ($pattern.confidence) {            "high" { "Green" }            "medium" { "Yellow" }            "low" { "Gray" }            default { "White" }        }        Write-Host "  [$($pattern.confidence.ToUpper())] $($pattern.type)" -ForegroundColor $color        Write-Host "      $($pattern.context)" -ForegroundColor DarkGray    }        if ($Approve -and ($SessionId -eq $session.timestamp)) {        $session.status = "approved"        $session | ConvertTo-Json -Depth 10 | Set-Content $file.FullName        Write-Host "`n  âœ“ Session approved!" -ForegroundColor Green    }}Write-Host "`n$('=' * 80)" -ForegroundColor Cyan$pendingCount = ($sessionFiles | Where-Object { (Get-Content $_.FullName | ConvertFrom-Json).status -eq 'pending_review' }).CountWrite-Host "Total pending sessions: $pendingCount" -ForegroundColor WhiteWrite-Host "`nTo approve a session:" -ForegroundColor YellowWrite-Host "  .\tools\review_learned_skills.ps1 -Approve -SessionId YYYYMMDD_HHMMSS" -ForegroundColor GrayWrite-Host "`nTo clear all pending:" -ForegroundColor Yellow$clearCmd = "Remove-Item `"$LearnedSkillsPath\session_*.json`""Write-Host "  $clearCmd" -ForegroundColor Gray
